import React from 'react'
import styles from './item.module.css'
import AppContext from 'utils/context'

const ListItem = ({ title, completed, indexElement, ...otherProps }) => (
  <AppContext.Consumer>
    {context =>
      <li className={completed ? [styles.item, styles.completed].join(' ') : styles.item} onClick={() => context.completeTodo(indexElement)} {...otherProps}>
        <span>{title}</span>
        <img
         src="data:image/svg+xml;base64,"
         onClick={ () => console.log(indexElement, 'teste') } />
      </li>
    }
  </AppContext.Consumer>
)

export default ListItem